/**
 * Tax Calculator Tests
 *
 * Comprehensive unit tests for tax calculation functions
 */

import { describe, it, expect } from 'vitest';
import {
  calculateFederalIncomeTax,
  applyStandardDeduction,
  applyBusinessLoss,
  applyRealEstateProfessional,
  applyOilInvestment,
  calculateTaxBurden,
  TAX_BRACKETS_2025,
