    it('should calculate effective tax rate correctly', () => {
      const state: TaxCalculatorState = {
        baseIncome: {
          w2Income: 500000,
          phantomEquityPayout: 0,
        },
        businessLoss: {
          enabled: false,
          lossAmount: 0,
        },
