"use client";

import { useMemo } from "react";
import { Share2, Target, TrendingDown, Clock, Heart } from "lucide-react";
import { CalculatorForm } from "@/components/die-with-zero/CalculatorForm";
import { NetWorthChart } from "@/components/die-with-zero/NetWorthChart";
import { CalculationBreakdown } from "@/components/die-with-zero/CalculationBreakdown";
import { Button } from "@/components/ui/button";
import { useUrlState } from "@/hooks/useUrlState";
import {
  calculateNetWorthProjection,
  aggregateToYearly,
} from "@/lib/die-with-zero-calculator";

export function DieWithZeroCalculator() {
  const { state, setState, isHydrated } = useUrlState();

  // Calculate projections
  const yearlyData = useMemo(() => {
    const monthlyProjection = calculateNetWorthProjection(state);
    return aggregateToYearly(monthlyProjection);
  }, [state]);

  // Calculate when money runs out
  const runOutAge = useMemo(() => {
    const firstNegative = yearlyData.find((d) => d.netWorth < 0);
    return firstNegative ? firstNegative.age : null;
  }, [yearlyData]);

  // Share functionality
  const handleShare = async () => {
    const shareData = {
      title: "Die with Zero Calculator - My Net Worth Projection",
      text: "Check out my financial projection using the Die with Zero Calculator",
      url: window.location.href,
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (err) {
        // User cancelled or error occurred
        console.log("Share cancelled or failed");
      }
    } else {
      // Fallback: copy to clipboard
      try {
        await navigator.clipboard.writeText(window.location.href);
        alert("Link copied to clipboard!");
      } catch (err) {
        console.error("Failed to copy:", err);
      }
    }
  };

  // Show loading state while hydrating
  if (!isHydrated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Loading calculator...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-16">
      <div className="container-small mx-auto px-4 md:px-8">
        <div className="mb-16">
          <div className="flex items-start justify-between mb-12">
            <div className="flex-1">
              <div className="inline-flex items-center gap-2 px-4 py-2 mb-6 bg-primary/10 backdrop-blur-sm rounded-full border border-primary/20">
                <Target className="h-4 w-4 text-primary" />
                <span className="text-sm font-medium text-primary">
                  Financial Philosophy
                </span>
              </div>
              <h1 className="text-5xl md:text-6xl font-bold mb-6 leading-tight">
                Die with{" "}
                <span className="bg-gradient-to-r from-primary via-primary/70 to-primary/50 bg-clip-text text-transparent">
                  Zero
                </span>
              </h1>
              <p className="text-xl text-muted-foreground leading-relaxed max-w-3xl">
                A financial calculator inspired by Bill Perkins&apos;
                philosophy: maximize your life experiences by optimally spending
                your wealth over your lifetime, rather than hoarding it.
              </p>
            </div>
            <Button
              onClick={handleShare}
              className="bg-primary hover:bg-primary/90 text-primary-foreground shadow-lg ml-6 flex-shrink-0"
              size="lg"
            >
              <Share2 className="h-5 w-5 mr-2" />
              Share This
            </Button>
          </div>

          <div className="grid md:grid-cols-3 gap-6">
            <div className="p-6 rounded-2xl bg-gradient-to-br from-primary/10 to-primary/5 border border-border">

'use client'

import { useEffect, useState } from 'react'
import { useRouter, useSearchParams } from 'next/navigation'
import type { IncomeExpenseEntry } from '@/lib/die-with-zero-calculator'

export interface CalculatorState {
  currentAge: number
  netWorth: number
  interestRate: number
  incomes: IncomeExpenseEntry[]
  expenses: IncomeExpenseEntry[]
}

const defaultState: CalculatorState = {
  currentAge: 40,
  netWorth: 500000,
  interestRate: 5,
  incomes: [
    { amount: 15000, frequency: 'monthly', startAge: 40, endAge: 65 },
  ],
  expenses: [
    { amount: 8000, frequency: 'monthly', startAge: 40, endAge: 80 },
  ],
}

/**
 * Encode state to base64 URL parameter
 */
function encodeState(state: CalculatorState): string {
  try {
    const json = JSON.stringify(state)
    return btoa(json)
  } catch {
    return ''
  }
}

/**
 * Decode base64 URL parameter to state
 */
function decodeState(encoded: string): CalculatorState | null {
  try {
    const json = atob(encoded)
    const parsed = JSON.parse(json)

    // Validate the structure
    if (
      typeof parsed.currentAge === 'number' &&
      typeof parsed.netWorth === 'number' &&
      typeof parsed.interestRate === 'number' &&
      Array.isArray(parsed.incomes) &&
      Array.isArray(parsed.expenses)
    ) {
      return parsed as CalculatorState
    }
    return null
  } catch {
    return null
  }
}

/**
 * Custom hook for managing calculator state in URL
 *
 * This hook:
 * 1. Reads state from URL on mount
 * 2. Provides state and setter
 * 3. Updates URL when state changes
 */
export function useUrlState() {
  const router = useRouter()
  const searchParams = useSearchParams()
  const [state, setStateInternal] = useState<CalculatorState>(defaultState)
  const [isHydrated, setIsHydrated] = useState(false)
  const [shouldSyncToUrl, setShouldSyncToUrl] = useState(false)

  // Load state from URL on mount
  useEffect(() => {
    const stateParam = searchParams.get('state')

    if (stateParam) {
      const decoded = decodeState(stateParam)
      if (decoded) {
        setStateInternal(decoded)
        setShouldSyncToUrl(true)
      }
    }

    setIsHydrated(true)
  }, [searchParams])

  // Custom setState that enables URL syncing
  const setState = (newState: CalculatorState | ((prev: CalculatorState) => CalculatorState)) => {
    setStateInternal(newState)
    setShouldSyncToUrl(true)
  }

  // Update URL when state changes (but only if sync is enabled)
  useEffect(() => {
    if (!isHydrated || !shouldSyncToUrl) return

    const encoded = encodeState(state)
    const currentParams = new URLSearchParams(searchParams.toString())

    if (encoded) {
      currentParams.set('state', encoded)
    } else {
      currentParams.delete('state')
    }

    const newUrl = `${window.location.pathname}?${currentParams.toString()}`
    router.replace(newUrl, { scroll: false })
  }, [state, router, searchParams, isHydrated, shouldSyncToUrl])

  return { state, setState, isHydrated }
}

