            </Label>
            <CardDescription className="mt-1">{description}</CardDescription>
          </div>
        </div>
      </CardHeader>

      {enabled && children && (
        <CardContent className="pt-0">
          <div className="space-y-4 pl-7">{children}</div>
        </CardContent>
      )}
    </Card>
  );
}

