        </div>

        {/* Strategy Education */}
        <div className="mb-12">
          <h2 className="text-2xl font-bold mb-6">Learn About Tax Strategies</h2>
          <StrategyEducation />
        </div>

        {/* Comprehensive Disclaimer Footer */}
        <div className="mt-16 space-y-4 p-6 rounded-xl bg-muted/30 border border-border">
