"use client";

import { Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { InputGroup } from "@/components/ui/input-group";
import { IncomeExpenseRow } from "./IncomeExpenseRow";
import type { CalculatorState } from "@/hooks/useUrlState";
import type { IncomeExpenseEntry } from "@/lib/die-with-zero-calculator";

interface CalculatorFormProps {
  state: CalculatorState;
  onChange: (state: CalculatorState) => void;
}

export function CalculatorForm({ state, onChange }: CalculatorFormProps) {
  // Format number with commas for display (allow empty string)
  const formatNumberForDisplay = (
    num: number,
    currentInput?: string
  ): string => {
    // If we have a current input and it's empty or just being typed, return it
    if (
      currentInput !== undefined &&
      (currentInput === "" || currentInput === "-")
    ) {
      return currentInput;
    }
    // If the number is 0 and we don't have explicit input, show empty
    if (num === 0 && currentInput === undefined) return "";
    return num.toLocaleString("en-US");
  };

  // Parse number string with commas to actual number
  const parseNumber = (str: string): number => {
    if (str === "" || str === "-") return 0;
    const cleaned = str.replace(/,/g, "");
    const num = parseFloat(cleaned);
    return isNaN(num) ? 0 : num;
  };

  // Filter input to only allow numbers, commas, and decimals
  const filterNumericInput = (value: string): string => {
    return value.replace(/[^0-9,.-]/g, "");
  };

  const handleAddIncome = () => {
    const newIncome: IncomeExpenseEntry = {
      amount: 5000,
      frequency: "monthly",
      startAge: state.currentAge,
      endAge: 65,
    };
    onChange({
      ...state,
      incomes: [...state.incomes, newIncome],
    });
  };

  const handleAddExpense = () => {
    const newExpense: IncomeExpenseEntry = {
      amount: 3000,
      frequency: "monthly",
      startAge: state.currentAge,
      endAge: 80,
    };
    onChange({
      ...state,
      expenses: [...state.expenses, newExpense],
    });
  };

  const handleUpdateIncome = (index: number, entry: IncomeExpenseEntry) => {
    const newIncomes = [...state.incomes];
    newIncomes[index] = entry;
    onChange({ ...state, incomes: newIncomes });
  };

  const handleRemoveIncome = (index: number) => {
    onChange({
      ...state,
      incomes: state.incomes.filter((_, i) => i !== index),
    });
  };

  const handleUpdateExpense = (index: number, entry: IncomeExpenseEntry) => {
    const newExpenses = [...state.expenses];
    newExpenses[index] = entry;
    onChange({ ...state, expenses: newExpenses });
  };

  const handleRemoveExpense = (index: number) => {
    onChange({
      ...state,
      expenses: state.expenses.filter((_, i) => i !== index),
    });
  };

  return (
    <div className="space-y-8">
      {/* Core Inputs */}
      <div className="grid md:grid-cols-3 gap-6">
        <div>
          <label className="text-sm font-medium mb-2 block">Current Age</label>
          <InputGroup
            type="text"
            value={state.currentAge === 0 ? "" : state.currentAge}
            onChange={(e) => {
              const filtered = filterNumericInput(e.target.value).replace(
                /[,.-]/g,
                ""
              );
              const num = filtered === "" ? 0 : Number(filtered);
              const clamped = Math.max(0, Math.min(100, num));
              onChange({ ...state, currentAge: clamped });
            }}
            className="h-12"
            placeholder="40"
          />
          <p className="text-xs text-muted-foreground mt-1">
            Your current age (18-100)
          </p>
        </div>

        <div>
          <label className="text-sm font-medium mb-2 block">
            Current Net Worth
          </label>
          <InputGroup
            type="text"
            prefix="$"
            value={
              state.netWorth === 0 ? "" : state.netWorth.toLocaleString("en-US")
            }
            onChange={(e) => {
              const filtered = filterNumericInput(e.target.value);
              onChange({ ...state, netWorth: parseNumber(filtered) });
            }}
            className="h-12"
            placeholder="1,000,000"
          />
          <p className="text-xs text-muted-foreground mt-1">
            Total assets minus debts
          </p>
        </div>

        <div>
          <label className="text-sm font-medium mb-2 block">
            Expected Interest Rate
          </label>
          <InputGroup
            type="text"
            suffix="%"
            value={state.interestRate === 0 ? "" : state.interestRate}
            onChange={(e) => {
              const filtered = filterNumericInput(e.target.value);
              onChange({
                ...state,
                interestRate: filtered === "" ? 0 : Number(filtered),
              });
            }}
            className="h-12"
            placeholder="5"
          />
          <p className="text-xs text-muted-foreground mt-1">
            Annual return on investments (e.g., 5 for 5%)
          </p>
        </div>
      </div>

      {/* Income Streams */}
      <div>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-bold">Income</h3>
          <Button onClick={handleAddIncome} size="sm" variant="outline">
            <Plus className="h-4 w-4 mr-2" />
            Add Income
          </Button>
        </div>

        <div className="space-y-3">
          {state.incomes.length === 0 ? (
            <div className="p-8 text-center bg-muted/20 rounded-xl border border-dashed border-border">
              <p className="text-sm text-muted-foreground">
                No income streams yet. Click &quot;Add Income&quot; to get
                started.
              </p>
            </div>
          ) : (
            state.incomes.map((income, index) => (
              <IncomeExpenseRow
                key={index}
                entry={income}
                onChange={(entry) => handleUpdateIncome(index, entry)}
                onRemove={() => handleRemoveIncome(index)}
              />
            ))
          )}
        </div>
      </div>

      {/* Expense Streams */}
      <div>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-bold">Expenses</h3>
          <Button onClick={handleAddExpense} size="sm" variant="outline">
            <Plus className="h-4 w-4 mr-2" />
            Add Expense
          </Button>
        </div>

        <div className="space-y-3">
          {state.expenses.length === 0 ? (
            <div className="p-8 text-center bg-muted/20 rounded-xl border border-dashed border-border">
              <p className="text-sm text-muted-foreground">
                No expenses yet. Click &quot;Add Expense&quot; to get started.
              </p>
            </div>
          ) : (
            state.expenses.map((expense, index) => (
              <IncomeExpenseRow
                key={index}
                entry={expense}
                onChange={(entry) => handleUpdateExpense(index, entry)}
                onRemove={() => handleRemoveExpense(index)}
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

"use client";

import { useMemo } from "react";
import { Share2, Target, TrendingDown, Clock, Heart } from "lucide-react";
import { CalculatorForm } from "@/components/die-with-zero/CalculatorForm";
import { NetWorthChart } from "@/components/die-with-zero/NetWorthChart";
import { CalculationBreakdown } from "@/components/die-with-zero/CalculationBreakdown";
import { Button } from "@/components/ui/button";
import { useUrlState } from "@/hooks/useUrlState";
import {
  calculateNetWorthProjection,
  aggregateToYearly,
} from "@/lib/die-with-zero-calculator";

export function DieWithZeroCalculator() {
  const { state, setState, isHydrated } = useUrlState();

  // Calculate projections
  const yearlyData = useMemo(() => {
    const monthlyProjection = calculateNetWorthProjection(state);
    return aggregateToYearly(monthlyProjection);
  }, [state]);

  // Calculate when money runs out
  const runOutAge = useMemo(() => {
    const firstNegative = yearlyData.find((d) => d.netWorth < 0);
    return firstNegative ? firstNegative.age : null;
  }, [yearlyData]);

  // Share functionality
  const handleShare = async () => {
    const shareData = {
      title: "Die with Zero Calculator - My Net Worth Projection",
      text: "Check out my financial projection using the Die with Zero Calculator",
      url: window.location.href,
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (err) {
        // User cancelled or error occurred
        console.log("Share cancelled or failed");
      }
    } else {
      // Fallback: copy to clipboard
      try {
        await navigator.clipboard.writeText(window.location.href);
        alert("Link copied to clipboard!");
      } catch (err) {
        console.error("Failed to copy:", err);
      }
    }
  };

  // Show loading state while hydrating
  if (!isHydrated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Loading calculator...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-16">
      <div className="container-small mx-auto px-4 md:px-8">
        <div className="mb-16">
          <div className="flex items-start justify-between mb-12">
            <div className="flex-1">
              <div className="inline-flex items-center gap-2 px-4 py-2 mb-6 bg-primary/10 backdrop-blur-sm rounded-full border border-primary/20">
                <Target className="h-4 w-4 text-primary" />
                <span className="text-sm font-medium text-primary">
                  Financial Philosophy
                </span>
              </div>
              <h1 className="text-5xl md:text-6xl font-bold mb-6 leading-tight">
                Die with{" "}
                <span className="bg-gradient-to-r from-primary via-primary/70 to-primary/50 bg-clip-text text-transparent">
                  Zero
                </span>
              </h1>
              <p className="text-xl text-muted-foreground leading-relaxed max-w-3xl">
                A financial calculator inspired by Bill Perkins&apos;
                philosophy: maximize your life experiences by optimally spending
                your wealth over your lifetime, rather than hoarding it.
              </p>
            </div>
            <Button
              onClick={handleShare}
              className="bg-primary hover:bg-primary/90 text-primary-foreground shadow-lg ml-6 flex-shrink-0"
              size="lg"
            >
              <Share2 className="h-5 w-5 mr-2" />
              Share This
            </Button>
          </div>

          <div className="grid md:grid-cols-3 gap-6">
            <div className="p-6 rounded-2xl bg-gradient-to-br from-primary/10 to-primary/5 border border-border">
              <div className="w-12 h-12 bg-primary/10 rounded-xl flex items-center justify-center mb-4">
                <TrendingDown className="h-6 w-6 text-primary" />
              </div>
              <h3 className="text-lg font-bold mb-2">Spend Intentionally</h3>
              <p className="text-sm text-muted-foreground leading-relaxed">
                Your wealth should be spent on experiences and helping others,
                not left unspent at the end of your life.
              </p>
            </div>

            <div className="p-6 rounded-2xl bg-gradient-to-br from-primary/10 to-primary/5 border border-border">
              <div className="w-12 h-12 bg-primary/10 rounded-xl flex items-center justify-center mb-4">
                <Clock className="h-6 w-6 text-primary" />
              </div>
              <h3 className="text-lg font-bold mb-2">Time is Finite</h3>
              <p className="text-sm text-muted-foreground leading-relaxed">
                Your health, energy, and time to enjoy experiences decline with
                age. Plan accordingly.
              </p>
            </div>

            <div className="p-6 rounded-2xl bg-gradient-to-br from-primary/10 to-primary/5 border border-border">
              <div className="w-12 h-12 bg-primary/10 rounded-xl flex items-center justify-center mb-4">
                <Heart className="h-6 w-6 text-primary" />
              </div>
              <h3 className="text-lg font-bold mb-2">Live Fully</h3>
              <p className="text-sm text-muted-foreground leading-relaxed">
                Balance saving for the future with living today. Don&apos;t
                delay experiences that matter.
              </p>
            </div>
          </div>
        </div>

        {/* Form */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold mb-6">Calculator Inputs</h2>
          <CalculatorForm state={state} onChange={setState} />
        </div>

        {/* Chart */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold mb-6">Net Worth Projection</h2>

          {/* Summary Stats */}
          {yearlyData.length > 0 && (
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="p-4 bg-muted/30 rounded-xl border border-border">
                <p className="text-sm text-muted-foreground mb-1">
                  Starting Net Worth
                </p>
                <p className="text-2xl font-bold">
                  {state.netWorth >= 1000000
                    ? `$${(state.netWorth / 1000000).toFixed(2)}M`
                    : `$${(state.netWorth / 1000).toFixed(0)}k`}
                </p>
                <p className="text-xs text-muted-foreground mt-1">
                  Your current net worth (before growth)
                </p>
              </div>

              {(() => {
                const fiveYearData = yearlyData.find(d => d.age === state.currentAge + 5);
                return fiveYearData ? (
                  <div className="p-4 bg-muted/30 rounded-xl border border-border">
                    <p className="text-sm text-muted-foreground mb-1">
                      After 5 Years
                    </p>
                    <p className="text-2xl font-bold">
                      {fiveYearData.netWorth >= 1000000
                        ? `$${(fiveYearData.netWorth / 1000000).toFixed(2)}M`
                        : fiveYearData.netWorth >= 0
                        ? `$${(fiveYearData.netWorth / 1000).toFixed(0)}k`
                        : `$0`}
                    </p>
                    <p className="text-xs text-muted-foreground mt-1">
                      Age {fiveYearData.age}
                    </p>
                  </div>
                ) : null;
              })()}

              {(() => {
                const tenYearData = yearlyData.find(d => d.age === state.currentAge + 10);
                return tenYearData ? (
                  <div className="p-4 bg-muted/30 rounded-xl border border-border">
                    <p className="text-sm text-muted-foreground mb-1">
                      After 10 Years
                    </p>
                    <p className="text-2xl font-bold">
                      {tenYearData.netWorth >= 1000000
                        ? `$${(tenYearData.netWorth / 1000000).toFixed(2)}M`
                        : tenYearData.netWorth >= 0
                        ? `$${(tenYearData.netWorth / 1000).toFixed(0)}k`
                        : `$0`}
                    </p>
                    <p className="text-xs text-muted-foreground mt-1">
                      Age {tenYearData.age}
                    </p>
                  </div>
                ) : null;
              })()}

              <div className="p-4 bg-muted/30 rounded-xl border border-border">
                <p className="text-sm text-muted-foreground mb-1">
                  Money Runs Out
                </p>
                <p
                  className={`text-2xl font-bold ${
                    runOutAge
                      ? "text-red-600 dark:text-red-500"
                      : "text-green-600 dark:text-green-500"
                  }`}
                >
                  {runOutAge ? `Age ${runOutAge}` : "Never"}
                </p>
              </div>
            </div>
          )}

          <div className="p-6 bg-muted/20 rounded-2xl border border-border">
            <NetWorthChart data={yearlyData} />
          </div>
        </div>

        {/* Calculation Breakdown */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold mb-6">Calculation Details</h2>
          <CalculationBreakdown inputs={state} />
        </div>

        {/* Financial Disclaimer */}
        <div className="mt-16">
          <h3 className="text-sm font-semibold text-gray-900 dark:text-gray-100 mb-3">
            Disclaimer
          </h3>
          <p className="text-xs text-gray-500 dark:text-gray-400 leading-relaxed">
            This calculator is provided for educational and informational purposes only and should not be considered financial, investment, tax, or legal advice. The projections are based on simplified assumptions and may not reflect actual future financial outcomes. Individual financial situations vary greatly, and many factors not considered in this calculator can significantly impact your financial future.
          </p>
          <p className="text-xs text-gray-500 dark:text-gray-400 leading-relaxed mt-2">
            We strongly recommend consulting with qualified financial advisors, tax professionals, and legal counsel before making any financial decisions. Past performance and mathematical projections do not guarantee future results. The creators of this calculator are not responsible for any financial decisions made based on its output.
          </p>
        </div>
      </div>
    </div>
  );
}

/**
 * Die with Zero Financial Calculator - Core Calculation Logic
 *
 * This module implements monthly compound interest calculations to project
 * net worth over a user's lifetime based on income streams, expenses, and
 * expected interest rates.
 *
 * Mathematical Formula:
 * For each month, net worth is updated as:
 * 1. Add active income for the month
 * 2. Subtract active expenses for the month
 * 3. Apply monthly compound interest: NW = NW × (1 + r/12)
 *
 * Where r is the annual interest rate as a decimal (e.g., 0.05 for 5%)
 */

export interface IncomeExpenseEntry {
  amount: number; // Monthly or annual amount
  frequency: 'monthly' | 'annual';
  startAge: number;
  endAge: number;
}

export interface CalculatorInputs {
  currentAge: number;
  netWorth: number;
  interestRate: number; // Annual rate as percentage (e.g., 5 for 5%)
  incomes: IncomeExpenseEntry[];
  expenses: IncomeExpenseEntry[];
}

export interface MonthlySnapshot {
  month: number; // Month index from start
  age: number; // Age at this month
  year: number; // Calendar year
  netWorth: number;
}

export interface YearlySnapshot {
  year: number;
  age: number;
  netWorth: number; // Net worth at end of year
}

/**
 * Get the current month (0-11, where 0 = January)
 */
export function getCurrentMonth(): number {
  return new Date().getMonth();
}

/**
 * Get the current year
 */
export function getCurrentYear(): number {
  return new Date().getFullYear();
}

/**
 * Convert annual interest rate (percentage) to monthly rate (decimal)
 * Example: 5% annual → 0.05 / 12 = 0.004167 monthly
 */
export function getMonthlyRate(annualRatePercent: number): number {
  return annualRatePercent / 100 / 12;
}

/**
 * Convert an income/expense entry to monthly amount
 */
export function toMonthlyAmount(entry: IncomeExpenseEntry): number {
  if (entry.frequency === 'monthly') {
    return entry.amount;
  }
  return entry.amount / 12;
}

/**
 * Check if an income/expense entry is active at a given age
 */
export function isActiveAtAge(entry: IncomeExpenseEntry, age: number): boolean {
  return age >= entry.startAge && age <= entry.endAge;
}

/**
 * Apply compound interest for one month
 */
export function applyCompoundInterest(
  currentNetWorth: number,
  monthlyRate: number
): number {
  return currentNetWorth * (1 + monthlyRate);
}

/**
 * Calculate the age (with decimal precision) for a given month index
 * Example: currentAge=40, monthIndex=0 → 40.0
 *          currentAge=40, monthIndex=6 → 40.5
 */
export function getAgeForMonth(currentAge: number, monthIndex: number): number {
  return currentAge + monthIndex / 12;
}

/**
 * Get the calendar year for a given month index
 */
export function getYearForMonth(startYear: number, startMonth: number, monthIndex: number): number {
  const totalMonths = startMonth + monthIndex;
  return startYear + Math.floor(totalMonths / 12);
}

/**
 * Calculate active monthly income for a given age
 */
export function getActiveIncome(incomes: IncomeExpenseEntry[], age: number): number {
  return incomes
    .filter(income => isActiveAtAge(income, age))
    .reduce((sum, income) => sum + toMonthlyAmount(income), 0);
}

/**
 * Calculate active monthly expenses for a given age
 */
export function getActiveExpenses(expenses: IncomeExpenseEntry[], age: number): number {
  return expenses
    .filter(expense => isActiveAtAge(expense, age))
    .reduce((sum, expense) => sum + toMonthlyAmount(expense), 0);
}

/**
 * Calculate the relevant maximum age to project based on income/expense ranges
 * Returns the latest endAge from all entries, or age 100 as fallback
 */
export function getRelevantMaxAge(
  currentAge: number,
  incomes: IncomeExpenseEntry[],
  expenses: IncomeExpenseEntry[]
): number {
  const allEntries = [...incomes, ...expenses];

  if (allEntries.length === 0) {
    return 100; // Default to 100 if no entries
  }

  const maxEndAge = Math.max(...allEntries.map(entry => entry.endAge));

  // Add a small buffer (5 years) to show what happens after income/expenses end
  // But cap at 100
  return Math.min(maxEndAge + 5, 100);
}

/**
 * Main calculation function: Projects net worth month-by-month from current age to relevant max age
 *
 * Algorithm (Standard Financial Planning Approach):
 * 1. Start from current month/year (handles partial years)
 * 2. For each month until relevant max age:
 *    a. Apply monthly compound interest to beginning balance
 *    b. Add active income (occurs at end of period, doesn't earn interest this month)
 *    c. Subtract active expenses (occurs at end of period)
 *    d. Record snapshot
 * 3. Return all monthly snapshots
 *
 * This order matches industry-standard financial calculators and is more conservative
 * than applying interest after cash flows.
 */
export function calculateNetWorthProjection(
  inputs: CalculatorInputs
): MonthlySnapshot[] {
  const { currentAge, netWorth, interestRate, incomes, expenses } = inputs;

  const monthlyRate = getMonthlyRate(interestRate);
  const currentMonth = getCurrentMonth();
  const currentYear = getCurrentYear();

  const snapshots: MonthlySnapshot[] = [];
  let currentNetWorth = netWorth;

  const maxAge = getRelevantMaxAge(currentAge, incomes, expenses);
  const totalMonths = (maxAge - currentAge) * 12;

  for (let monthIndex = 0; monthIndex < totalMonths; monthIndex++) {
    const age = getAgeForMonth(currentAge, monthIndex);
    const year = getYearForMonth(currentYear, currentMonth, monthIndex);

    // Apply compound interest FIRST (to beginning balance)
    // This is the standard approach used by financial planners
    currentNetWorth = applyCompoundInterest(currentNetWorth, monthlyRate);

    // Add income for this month (doesn't earn interest until next month)
    const monthlyIncome = getActiveIncome(incomes, age);
    currentNetWorth += monthlyIncome;

    // Subtract expenses for this month
    const monthlyExpenses = getActiveExpenses(expenses, age);
    currentNetWorth -= monthlyExpenses;

    // Record snapshot
    snapshots.push({
      month: monthIndex,
      age: Math.floor(age),
      year,
      netWorth: currentNetWorth,
    });
  }

  return snapshots;
}

/**
 * Aggregate monthly snapshots into yearly snapshots for chart display
 * Takes the last month of each year as the year's net worth
 */
export function aggregateToYearly(monthly: MonthlySnapshot[]): YearlySnapshot[] {
  const yearlyMap = new Map<number, MonthlySnapshot>();

  // Group by age and keep the last snapshot for each age
  monthly.forEach(snapshot => {
    yearlyMap.set(snapshot.age, snapshot);
  });

  // Convert to array and sort by age
  return Array.from(yearlyMap.values())
    .map(snapshot => ({
      year: snapshot.year,
      age: snapshot.age,
      netWorth: snapshot.netWorth,
    }))
    .sort((a, b) => a.age - b.age);
}

{
  "compilerOptions": {
    "target": "ES2017",
    "lib": ["dom", "dom.iterable", "esnext"],
    "allowJs": true,
    "skipLibCheck": true,
    "strict": true,
    "noEmit": true,
    "esModuleInterop": true,
    "module": "esnext",
    "moduleResolution": "bundler",
    "resolveJsonModule": true,
    "isolatedModules": true,
    "jsx": "preserve",
    "incremental": true,
    "plugins": [
      {
        "name": "next"
      }
    ],
    "paths": {
      "@/*": ["./src/*"]
    }
  },
  "include": ["next-env.d.ts", "**/*.ts", "**/*.tsx", ".next/types/**/*.ts"],
  "exclude": ["node_modules"]
}
